(function($){'use strict';$.fn.LineProgressbar=function(options){options=$.extend({percentage:null,ShowProgressCount:!0,duration:1000,fillBackgroundColor:'#3498db',backgroundColor:'#f0f0f0',radiusFill:'15px 0 0 15px',radiusBar:'15px',height:'20px',width:'100%'},options);$.options=options;return this.each(function(index,el){$(el).html('<div class="progressbar"><div class="proggress"><div class="percentCount"></div></div></div>');var progressFill=$(el).find('.proggress');var progressBar=$(el).find('.progressbar');progressFill.css({backgroundColor:options.fillBackgroundColor,height:options.height,borderRadius:options.radiusFill});progressBar.css({width:options.width,backgroundColor:options.backgroundColor,borderRadius:options.radiusBar});progressFill.animate({width:options.percentage+"%"},{step:function(x){if(options.ShowProgressCount){$(el).find(".percentCount").text(Math.round(x)+"%")}},duration:options.duration})})}
$.fn.progressTo=function(next){var options=$.options;return this.each(function(index,el){var progressFill=$(el).find('.proggress');var progressBar=$(el).find('.progressbar');progressFill.animate({width:next+"%"},{step:function(x){if(options.ShowProgressCount){$(el).find(".percentCount").text(Math.round(x)+"%")}},duration:options.duration})})}})(jQuery)

